","keccak256":"0x9f4357008a8f7d8c8bf5d48902e789637538d8c016be5766610901b4bba81514","license":"MIT"},"contracts/library/TimelockRoles.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

library TimelockRoles {
  bytes32 public constant TERMINATION_ADMIN_ROLE = keccak256("TERMINATION_ADMIN");
  bytes32 public constant TIMELOCK_CREATOR_ROLE = keccak256("TIMELOCK_CREATOR");
}","keccak256":"0x04c57830c1483f67d25bf503cab124f0d87d21ca76cc8540dea997229e0675d3","license":"MIT"},"contracts/timelock/BatchTimelock.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {TimelockRoles} from "../library/TimelockRoles.sol";
import "./ITerminateable.sol";
import "./IBatchTimelock.sol";
import "./IVestingPool.sol";

contract BatchTimelock is ITerminateable, IBatchTimelock, IVestingPool, AccessControl {
    using EnumerableSet for EnumerableSet.AddressSet;

    /**
     * @notice Token that will be vested (IQT).
     * @dev Link to IQT repository: https://github.com/iqlabsorg/iqt-eth
     */
    IERC20 internal _token;

    /**
     * @notice Address of the vesting pool.
     */
    address internal _vestingPool;

    /**
     * @notice Array of all receiver addresses.
     */
    EnumerableSet.AddressSet internal _allReceivers;

    /**
     * @notice Mapping of all timelocks.
     */
    mapping(address => Timelock) internal _timelocks;

    /**
     * @notice Checks if the timelock exists
     */
    modifier onlyReceiver() {
        if (_timelocks[_msgSender()].receiver != _msgSender()) {
            revert InvalidReceiverAddress();
        }
        _;
    }

    modifier onlyTimelockCreator() {
        if (!hasRole(TimelockRoles.TIMELOCK_CREATOR_ROLE, _msgSender()) && !hasRole(DEFAULT_ADMIN_ROLE, _msgSender())) {
            revert CallerIsNotATimelockCreator();
        }
        _;
    }

    modifier onlyTerminationAdmin() {
        if (!hasRole(TimelockRoles.TERMINATION_ADMIN_ROLE, _msgSender()) && !hasRole(DEFAULT_ADMIN_ROLE, _msgSender())) {
            revert CallerIsNotATerminationAdmin();
        }
        _;
    }

    /**
     * @notice Reverts if the timelock does not exist.
    */
    constructor(IERC20 token, address vestingPool) {
        _token = token;
        _vestingPool = vestingPool;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(TimelockRoles.TERMINATION_ADMIN_ROLE, _msgSender());
        _setupRole(TimelockRoles.TIMELOCK_CREATOR_ROLE, _msgSender());
    }

    /**
     * @inheritdoc ITerminateable
     */
    function terminate(address receiver, uint256 terminationFrom) external onlyTerminationAdmin {
        Timelock storage lock = _timelocks[receiver];

        if (lock.timelockFrom >= terminationFrom) {
            revert TerminationTimeMustBeAfterLockStart(terminationFrom, lock.timelockFrom);
        }

        lock.terminationFrom = terminationFrom;
        lock.isTerminated = true;

        emit TimelockTerminated(receiver, terminationFrom);
    }

    /**
     * @inheritdoc ITerminateable
     */
    function determinate(address receiver) external onlyTerminationAdmin {
        Timelock storage lock = _timelocks[receiver];
        lock.isTerminated = false;
        lock.terminationFrom = 0;

        emit TimelockDeterminated(receiver);
    }

    /**
     * @inheritdoc IBatchTimelock
    */
    function addTimelockBatch(Receiver[] memory receivers) external onlyTimelockCreator {
        if (receivers.length == 0) {
            revert EmptyReceiversArray();
        }

        uint256 receiversCount = receivers.length; // Caching the array length outside a loop
        unchecked {
            for (uint256 i = 0; i < receiversCount; ++i) {
                _addTimelock(
                    receivers[i].receiver,
                    receivers[i].totalAmount,
                    receivers[i].timelockFrom,
                    receivers[i].cliffDuration,
                    receivers[i].vestingDuration
                );
            }
        }
    }

    /**
     * @inheritdoc IBatchTimelock
    */
    function addTimelock(
        address receiver,
        uint256 totalAmount,
        uint256 timelockFrom,
        uint256 cliffDuration,
        uint256 vestingDuration
    ) external onlyTimelockCreator {
        _addTimelock(receiver, totalAmount, timelockFrom, cliffDuration, vestingDuration);
    }

    /**
     * @inheritdoc IBatchTimelock
     */
    function claim(uint256 amount) external onlyReceiver {
        if (amount == 0) {
            revert ZeroClaimAmount();
        }

        Timelock storage lock = _timelocks[_msgSender()];
        uint256 blockTimestamp = block.timestamp;

        if (blockTimestamp < lock.timelockFrom + lock.cliffDuration) {
            revert CliffPeriodNotEnded(blockTimestamp, lock.timelockFrom + lock.cliffDuration);
        }

        uint256 withdrawable = getClaimableBalance(_msgSender());

        if (amount > withdrawable) {
            revert AmountExceedsWithdrawableAllowance(amount, withdrawable);
        }

        lock.releasedAmount += amount;

        _token.transferFrom(_vestingPool, _msgSender(), amount);

        emit TokensClaimed(_msgSender(), amount);
    }

    /**
     * @inheritdoc IBatchTimelock
     */
    function getClaimableBalance(address receiver) public view returns (uint256) {
        Timelock storage lock = _timelocks[receiver];
        uint256 lockFromPlusCliff = lock.timelockFrom + lock.cliffDuration;
        uint256 blockTimestampNow = block.timestamp;

        if (blockTimestampNow < lockFromPlusCliff) {
            return 0;
        }

        uint256 vestedTime;
        if (lock.isTerminated) {
            vestedTime = lock.terminationFrom > lockFromPlusCliff
                ? lock.terminationFrom - lockFromPlusCliff
                : 0;
        } else {
            uint256 vestingEnd = lockFromPlusCliff + lock.vestingDuration;
            vestedTime = blockTimestampNow < vestingEnd
                ? blockTimestampNow - lockFromPlusCliff
                : lock.vestingDuration;
        }
        uint256 vestedPortion = (lock.totalAmount * vestedTime) / lock.vestingDuration;

        uint256 claimable = vestedPortion > lock.releasedAmount
            ? vestedPortion - lock.releasedAmount
            : 0;

        if (lock.isTerminated && blockTimestampNow >= lock.terminationFrom) {
            return claimable;
        }

        return claimable;
    }


    /**
     * @inheritdoc IBatchTimelock
    */
    function getTimelock(address receiver) public view returns (Timelock memory) {
        return _timelocks[receiver];
    }

    /**
     * @inheritdoc IBatchTimelock
    */
    function getTimelockReceivers(uint256 offset, uint256 limit) external view returns (address[] memory) {
        uint256 receiverCount = _allReceivers.length();
        if (offset >= receiverCount) {
            return new address[](0);
        }

        if (offset + limit > receiverCount) {
            limit = receiverCount - offset;
        }

        address[] memory receivers = new address[](limit);
        unchecked {
            for (uint256 i = 0; i < limit; ++i) {
                receivers[i] = _allReceivers.at(offset + i);
            }
        }
        return receivers;
    }

    /**
     * @inheritdoc IBatchTimelock
    */
    function getTimelockReceiversAmount() external view returns (uint256) {
        return _allReceivers.length();
   }

    /**
     * @inheritdoc IVestingPool
     */
    function getCurrentAllowance() public view returns (uint256) {
        return _token.allowance(_vestingPool, address(this));
    }

    /**
     * @inheritdoc IVestingPool
     */
    function getTotalTokensLocked() public view returns (uint256) {
        uint256 total = 0;
        uint256 receiverCount = _allReceivers.length(); // Caching the array length outside a loop
        unchecked {
            for (uint256 i = 0; i < receiverCount; ++i) {
                total += _timelocks[_allReceivers.at(i)].totalAmount;
            }
        }
        return total;
    }

    /**
     * @inheritdoc IVestingPool
     */
    function getVestingPoolAddress() public view returns (address) {
        return _vestingPool;
    }

    /**
     * @inheritdoc IVestingPool
     */
    function getTokenAddress() public view returns (address) {
        return address(_token);
    }

    /**
     * @notice Creates new timelock for the receiver.
     * @param receiver Address of the receiver.
     * @param totalAmount Total amount of tokens to be vested.
     * @param timelockFrom Timestamp from which the timelock will start.
     * @param cliffDuration Cliff time in seconds (6 months default).
     * @param vestingDuration Vesting duration in seconds (18/24 months).
    */
    function _addTimelock(address receiver, uint256 totalAmount, uint256 timelockFrom, uint256 cliffDuration, uint256 vestingDuration) internal onlyTimelockCreator {
        if (receiver == address(0)) revert InvalidReceiverAddress();
        if (totalAmount == 0) revert InvalidTimelockAmount();
        if (_allReceivers.contains(receiver)) revert ReceiverAlreadyHasATimelock(receiver);

        _timelocks[receiver] = Timelock({
            receiver: receiver,
            totalAmount: totalAmount,
            releasedAmount: 0,
            timelockFrom: timelockFrom,
            cliffDuration: cliffDuration,
            vestingDuration: vestingDuration,
            isTerminated: false,
            terminationFrom: 0
        });
        _allReceivers.add(receiver);

        emit TimelockCreated(receiver, totalAmount, timelockFrom, cliffDuration, vestingDuration);
    }
}","keccak256":"0x46d9fc6a8bde71a5a7030e0cbb8ca8097cfc4509e70b12a69d8bdc6c36088d4f","license":"MIT"},"contracts/timelock/IBatchTimelock.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IBatchTimelock {
    /**
     * @notice Reverts if amount exceeds vesting allowance.
     * @param amount Amount of tokens to be claimed.
     * @param claimable Amount of tokens that can be claimed.
     */
    error AmountExceedsWithdrawableAllowance(uint256 amount, uint256 claimable);

    /**
     * @notice Reverts if cliff period has not ended.
     */
    error CliffPeriodNotEnded(uint256 timeNow, uint256 cliffEndsAt);

    /**
     * @notice Reverts if vesting period has not started.
     */
    error ZeroAllocation();

    /**
     * @notice Reverts if empty receivers array is passed.
     */
    error EmptyReceiversArray();

    /**
     * @notice Reverts if invalid receiver address is passed.
     */
    error InvalidReceiverAddress();

    /**
     * @notice Reverts if invalid timelock amount is passed.
     */
    error InvalidTimelockAmount();

    /**
     * @notice Reverts if receiver already has a timelock.
     * @param receiver Address of the receiver.
     */
    error ReceiverAlreadyHasATimelock(address receiver);

    /**
     * @notice Reverts if zero claim amount is passed.
    */
    error ZeroClaimAmount();

    /**
     * @notice Reverts if token transfer failed.
     * @param tokenPool Address of the token pool.
     * @param receiver Address of the receiver.
     * @param amount Amount of tokens transferred.
    */
    error TokenTransferFailed(address tokenPool, address receiver, uint256 amount);

    /**
     * @dev Reverts if the caller is not a termination admin.
     */
    error CallerIsNotATerminationAdmin();

    /**
     * @dev Reverts if the caller is not the timelock creator.
     */
    error CallerIsNotATimelockCreator();

    /**
     * @notice Token receiver struct that is used for adding _timelocks for timelock.
     * @param receiver Address of the receiver.
     * @param totalAmount Total amount of tokens to be vested.
     * @param timelockFrom Timestamp from which the timelock will start.
     * @param cliffDuration Cliff time in months (6 months default).
     * @param vestingDuration Vesting duration in months (18/24 months).
     */
    struct Receiver {
        address receiver;
        uint256 totalAmount;
        uint256 timelockFrom;
        uint256 cliffDuration;
        uint256 vestingDuration;
    }

    /**
     * @notice Timelock struct that is used for vesting.
     * @param receiver Address of the receiver.
     * @param totalAmount Total amount of tokens to be vested.
     * @param releasedAmount Amount of tokens released.
     * @param lockFrom Timestamp from which tokens will start to vest.
     * @param cliffDuration Cliff time in months (6 months default).
     * @param vestingDuration Vesting duration in months (18/24 months).
     * @param terminationFrom Timestamp from which tokens will be terminated.
     * @param isTerminated Flag that indicates if timelock is terminated.
     */
    struct Timelock {
        address receiver;
        uint256 totalAmount;
        uint256 releasedAmount;
        uint256 timelockFrom;
        uint256 cliffDuration;
        uint256 vestingDuration;
        uint256 terminationFrom;
        bool isTerminated;
    }

    /**
     * @notice Emits when timelock is created.
     * @param receiver Address of the receiver.
     * @param totalAmount Total amount of tokens to be vested.
     * @param cliffDuration Cliff time in seconds (6 months default).
     * @param vestingDuration Vesting duration in seconds (18/24 months).
     */
    event TimelockCreated(address indexed receiver, uint256 totalAmount, uint256 timelockFrom, uint256 cliffDuration, uint256 vestingDuration);

    /**
     * @notice Emits when tokens are claimed.
     * @param receiver Address of the claimer.
     * @param amount Amount of tokens claimed.
     */
    event TokensClaimed(address indexed receiver, uint256 amount);

    /**
     * @notice Creates timelock for token receivers in a batch mode.
     * @param receivers Array of receivers.
     */
    function addTimelockBatch(Receiver[] memory receivers) external;

    /**
     * @notice Creates timelock for token receiver.
     * @param receiver Address of the receiver.
     * @param totalAmount Total amount of tokens to be vested.
     * @param timelockFrom Timestamp from which the timelock will start.
     * @param cliffDuration Cliff time in seconds (6 months default).
     * @param vestingDuration Vesting duration in seconds (18/24 months).
    */
    function addTimelock(address receiver, uint256 totalAmount, uint256 timelockFrom, uint256 cliffDuration, uint256 vestingDuration) external;

    /**
     * @notice Claims tokens for the receiver.
     * @param amount Amount of tokens to be claimed.
     */
    function claim(uint256 amount) external;

    /**
     * @notice Returns the timelock data.
     * @param receiver Address of the receiver.
     */
    function getTimelock(address receiver) external view returns (Timelock memory);

    /**
     * @notice Returns the amount of tokens that are currently allowed for claim.
     * @param receiver Address of the receiver.
     */
    function getClaimableBalance(address receiver) external view returns (uint256);

    /**
     * @notice Returns the array of timelock receivers.
     * @param offset Offset from which receivers will be returned.
     * @param limit Limit of receivers to be returned.
     */
    function getTimelockReceivers(uint256 offset, uint256 limit) external view returns (address[] memory);

    /**
     * @notice Returns the amount of timelock receivers.
     */
    function getTimelockReceiversAmount() external view returns (uint256);
}","keccak256":"0xc1afc10b148956bf031804d150ac6bd2c69d83a6071a83b1fdeb0b205187f33a","license":"MIT"},"contracts/timelock/ITerminateable.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface ITerminateable {
    /**
     * @notice Reverts if termination time is before lock start.
     * @param terminationFrom Timestamp from which tokens will be terminated.
     * @param lockFrom Timestamp from which tokens will be locked.
    */
    error TerminationTimeMustBeAfterLockStart(uint256 terminationFrom, uint256 lockFrom);

    /**
     * @notice Emits when timelock is terminated.
     * @param receiver Address of the receiver.
     * @param terminationFrom Timestamp from which tokens will be terminated.
     */
    event TimelockTerminated(address indexed receiver, uint256 terminationFrom);

    /**
     * @notice Emits when timelock is determinated.
     * @param receiver Address of the receiver.
     */
    event TimelockDeterminated(address indexed receiver);

    /**
     * @notice Terminates timelock for the receiver.
     * @param receiver Address of the receiver.
     * @param terminationFrom Timestamp from which tokens will be terminated.
     */
    function terminate(address receiver, uint256 terminationFrom) external;

    /**
     * @notice Determinates timelock for the receiver.
     * @param receiver Address of the receiver.
     */
    function determinate(address receiver) external;
}","keccak256":"0xe43c3d6eca24728a9b6795007f1cd99f2b3d39912715a0c6396c20d6bb415200","license":"MIT"},"contracts/timelock/IVestingPool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IVestingPool {
    /**
     * @notice Returns the amount of tokens that are currently allowed for transfer.
     */
    function getCurrentAllowance() external view returns (uint256);

    /**
     * @notice Returns the amount of tokens that are currently locked.
     */
    function getTotalTokensLocked() external view returns (uint256);

    /**
     * @notice Returns the address of vesting pool.
     */
    function getVestingPoolAddress() external view returns (address);

    /**
     * @notice Returns the address of token.
     */
    function getTokenAddress() external view returns (address);
}","keccak256":"0xac78e174e43794a5e253990f90f4077f2594902eb88a517b271729fee0b670a0","license":"MIT"}},"version":1}
